SELECT CO.courseOfferId, CO.CourseId, CO.year, CO.Quartile, C.CourseName, C.CourseDescription, C.DegreeId, C.ECTS, D.Dept, D.DegreeDescription, D.TotalECTS, TA.TeacherId, T.TeacherName, T.Address, T.BirthyearTeacher, T.Gender FROM courses C, courseOffers CO, Degrees D, TeacherAssignmentsToCourses TA, Teachers T WHERE CO.courseOfferId = 1 and C.courseid = CO.courseid and C.DegreeId = D.DegreeId and TA.courseOfferId = CO.courseOfferId and T.TeacherId = TA.TeacherId;
SELECT CO.CourseOfferId, CO.CourseId, CO.Year, CO.Quartile, S.StudentId, S.StudentName, S.Address, S.BirthyearStudent, S.Gender, D.DegreeId, D.Dept, D.DegreeDescription, D.TotalECTS FROM StudentAssistants SA, CourseOffers CO, Students S, StudentRegistrationsToDegrees SD, Degrees D WHERE SA.StudentRegistrationId = 3 and SA.CourseOfferId = CO.CourseOfferId and SA.StudentRegistrationId = SD.StudentRegistrationId and SD.Studentid = S.StudentId and D.DegreeId = SD.DegreeId;
SELECT avg(CR.grade) FROM CourseRegistrations CR WHERE CR.StudentRegistrationId = 3;
